DROP TABLE IF EXISTS `eukary`;
CREATE TABLE `eukary` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `taxonomy` text,
  `common` text,
  PRIMARY KEY (`id`)
) ENGINE=INNODB;

DROP TABLE IF EXISTS `note`;
CREATE TABLE `note` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `dt` datetime NOT NULL,
  `location` text,
  `localdt` text,
  `html` text,
  `euk_id` int(10) unsigned,
  `con_id` int(10) unsigned,
  `upl_id` int(10) unsigned,
  PRIMARY KEY (`id`)
) ENGINE=INNODB;

DROP TABLE IF EXISTS `upload`;
CREATE TABLE `upload` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `filen` varchar(120),
  `exten` varchar(12),
  `dt` datetime,
  `localdt` text,
  `location` text,
  `name` text,
  PRIMARY KEY (`id`)
) ENGINE=INNODB;

DROP TABLE IF EXISTS `contact`;
CREATE TABLE `contact` (
  `id` int(10) unsigned NOT NULL auto_increment,
  `name` text,
  PRIMARY KEY (`id`)
) ENGINE=INNODB;

DROP TABLE IF EXISTS `contact_euk`;
CREATE TABLE `contact_euk` (
  `con_id` int(10) unsigned NOT NULL,
  `euk_id` int(10) unsigned NOT NULL,
  `certain` float,
  PRIMARY KEY (`con_id`,`euk_id`)
) ENGINE=INNODB;

DROP TABLE IF EXISTS `contact_upl`;
CREATE TABLE `contact_upl` (
  `con_id` int(10) unsigned NOT NULL,
  `upl_id` int(10) unsigned NOT NULL,
  `certain` float,
  PRIMARY KEY (`con_id`,`upl_id`)
) ENGINE=INNODB;

