DROP TABLE IF EXISTS `vocaliz`;
CREATE TABLE `vocaliz` (
  `id` int(10) unsigned NOT NULL,
  `info` text,
  PRIMARY KEY (`id`)
) ENGINE=INNODB;

DROP TABLE IF EXISTS `clip_diff`;
CREATE TABLE `clip_diff` (
  `upl1_id` int(10) unsigned NOT NULL,
  `upl2_id` int(10) unsigned NOT NULL,
  `diff` float,
  KEY (`diff`),
  PRIMARY KEY  (`upl1_id`,`upl2_id`)
) ENGINE=INNODB;






